import React from 'react';
import './Card.css';
import clubImage from './Images/club.png';
import spadeImage from './Images/spade.png';
import diamondImage from './Images/diamond.png';
import heartImage from './Images/heart.png';
import {Col} from 'react-bootstrap';

function Card(props){
    const cardInformation = props.name;
    var suitType = getSuitType(props.suitType);


    
    return(
        
          <Col xs={6}>{cardInformation}<img id = "CardSuit" src = {suitType} alt = "Suit"></img></Col>
         
      
    )
}

function getSuitType(suitType){
    switch(suitType){
        case "clubs":
            return clubImage;
        case "spades":
            return spadeImage;
        case "hearts":
            return heartImage;
        case "diamonds":
            return diamondImage;
    }
}

export default Card;
