import React from 'react';
import './Card.css';
import clubImage from './Images/club.png';
import spadeImage from './Images/spade.png';
import diamondImage from './Images/diamond.png';
import heartImage from './Images/heart.png';

function Card(props){
    const cardInformation = props.name;
    var suitType = getSuitType();


    
    return(
        <div className = "SingleCard">
          <span><div className = "CardNumber">{cardInformation}<img id = "CardSuit" src = {clubImage} alt = "Suit"></img></div></span>
        </div>
    )
}

function getSuitType(){
    switch(props.suitType){
        case "clubs":
            return clubImage;
        case "spades":
            return spadeImage;
        case "hearts":
            return heartImage;
        case "diamonds":
            return diamondImage;
    }
}

export default Card;
